CREATE TABLE `book` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `titre` varchar(255) CHARACTER SET latin1 NOT NULL,
  `auteur` varchar(255) CHARACTER SET latin1 NOT NULL,
  `annee` varchar(255) CHARACTER SET latin1 NOT NULL,
  `theme` varchar(255) CHARACTER SET latin1 NOT NULL,
  `resume` varchar(5000) NOT NULL,
  `tome` int(10) NOT NULL,
  `page` varchar(255) DEFAULT NULL,
  `format` varchar(255) NOT NULL,
  `date_lecture` varchar(255) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=95 DEFAULT CHARSET=utf8;



